import './App.css';
import {Route,Switch,BrowserRouter as Router} from "react-router-dom"
import {userContext} from './context/context'
import { useState,useEffect } from 'react';
import Home from './Routes/Home';
import Login from './Routes/Login';
import Reg from './Routes/Reg';
import Axios from 'axios'
import { toast, ToastContainer } from 'react-toastify';
function App() {
  var [token,setToken] = useState(localStorage.getItem('token'));
  var [user,setUser] = useState('');
  useEffect(()=>{
    (async()=>{
      if(token){
        try {
          let res = await Axios({
            method:"GET",
            url:"http://localhost:5000/api/user/",
            headers:{
              "auth":token
            }
          });

          if(res.data.status){
            setUser(res.data);
          }else{
            setUser('');
            setToken('');
            localStorage.removeItem('token');
          }
        } catch (error) {
          toast('Please Connect to Internet',{type:"error",position:"bottom-right"});
        }
      }
    })();
  },[token]);
  return (
    <userContext.Provider value = {{token,setToken,user,setUser}}>
      <ToastContainer />
      <Router>
        <Switch>
          <Route exact path="/" component={Reg} />
          <Route exact path="/home" component={Home} />
          <Route exact path="/log-nuser" component={Login} />
        </Switch>
      </Router>
    </userContext.Provider>
  );
}

export default App;
