  //           localStorage.removeItem('token');
  //         }
  //       } catch (error) {
  //         toast('Please Connect to Internet',{type:"error",position:"bottom-right"});
  //       }
  //     }
  //   })();
  // },[token]);
  return (
    <userContext.Provider value = {{token,setToken,user,setUser,handleLogout,socket,peer}}>
      <ToastContainer />
      <Router>
        <Switch>
          <Route exact path="/" component={Reg} />
          <Route exact path="/home" component={Home} />
          <Route exact path="/log-nuser" component={Login} />
        </Switch>
      </Router>
    </userContext.Provider>
