import React, { useContext, useState } from 'react'
import { Row, Col, Form, Input, InputGroup, CardHeader, Button } from 'reactstrap'
import axios from 'axios';
import {userContext} from './../../context/context'
import { Redirect } from 'react-router-dom';
import { toast } from 'react-toastify';
export default function ProfileForm() {
    const [file, setFile] = useState('');
    const [name,setName] = useState('');
    const [email,setEmail] = useState('');
    const [number,setNumber] = useState('');
    const [work,setWork] = useState('');
    const [nikName,setNikname] = useState('');
    const [status,setStatus] = useState('');
    const [skill,setSkills] = useState('');
    const [about,setAbout] = useState('');
    const [bio,setBio] = useState('');
    const context = useContext(userContext);
    const [redir,setRedir] = useState(0);
    const submitHandler = e => {
        e.preventDefault()
        let formData = new Form();
        formData.append('image', file);
        formData.append('name', name);
        formData.append('about',about);
        formData.append('bio',bio);
        formData.append('number',number);
        formData.append('email',email);
        formData.append('work',work);
        formData.append('skills',skill);
        formData.append('nikname',nikName);
        formData.append('status',status);
        try {
            (async () => {
                const res = await axios({
                    method:"POST",
                    url:"http://localhost:5000/api/user/add/profile",
                    headers:{
                        'accept':'application/json',
                        'Accept-Language':'en-US,en;q=0.8',
                        'Content-Type':`multipart/form-data; boundary=${formData._boundary}`,
                        auth:context.token
                    },
                    body:{
                        'name':name,
                        'about':about,
                        'bio':bio,
                        'number':number,
                        'email':email,
                        'work':work,
                        'skills':skill,
                        'nikname':nikName,
                        'status':status,
                        'image':file
                    }
                })
                if(res.status===201){
                    toast('New Profile Added Successfully..', {type:"success",position:"bottom-right"});
                    setRedir(1);
                }
            })()
        } catch (error) {
            toast('Something went wrong',{type:"error",position:"bottom-right"});
        }
    }

    return (
        <div>
            {redir && <Redirect to="/admin/edit-profile" />}
            <Row className="mt-5">
                <Col md={2}></Col>
                <Col md={8}>
                    <CardHeader>
                        <b>
                            <h3>Add New Profile</h3>
                        </b>
                    </CardHeader>
                    <Form onSubmit={submitHandler} id="addPortfoio">
                        <InputGroup>
                            <Input className="from-control mt-3" placeholder="Enter Your Name" onChange={e=>{setName(e.target.value)}} type="text"  />
                        </InputGroup>
                        <InputGroup>
                            <Input className="from-control mt-3" placeholder="Select Image" type="file" onChange={e => {setFile(e.target.files[0])}}  />
                        </InputGroup>

                        <InputGroup>
                            <Input className="from-control mt-3" placeholder="Enter Your Email" onChange={e=>setEmail(e.target.value)} type="email"  />
                        </InputGroup>
                        <InputGroup>
                            <Input className="from-control mt-3" placeholder="Enter Your Number" onChange={e=>setNumber(e.target.value)} type="text"  />
                        </InputGroup>
                        <InputGroup>
                            <Input className="from-control mt-3" placeholder="Enter Your Work" onChange={e=>{setWork(e.target.value)}} type="text"  />
                        </InputGroup>
                        <InputGroup>
                            <Input className="from-control mt-3" placeholder="Enter Your Nikname" onChange={e=>{setNikname(e.target.value)}} type="text"  />
                        </InputGroup>
                        <InputGroup>
                            <Input className="from-control mt-3" placeholder="Enter Your Status" onChange={e=>{setStatus(e.target.value)}} type="text"  />
                        </InputGroup>
                        <InputGroup>
                            <Input type="textarea" className="mt-3" placeholder="Add Your Skills" onChange={e=>{setSkills(e.target.value)}}  />
                        </InputGroup>
                        <InputGroup>
                            <Input type="textarea" className="mt-3" placeholder="About Yourself" onChange={e=>{setAbout(e.target.value)}}  />
                        </InputGroup>
                        <InputGroup>
                            <Input type="textarea" className="mt-3" placeholder="Biography" onChange={e=>{setBio(e.target.value)}}  />
                        </InputGroup>

                        <InputGroup>
                            <Button className="btn btn-success mt-3">Add</Button>
                        </InputGroup>
                    </Form>
                </Col>
                <Col md={2}></Col>
            </Row>
        </div>
    )
}
