import { CircularProgress, Icon } from '@material-ui/core';
import React, { useContext, useEffect, useState } from 'react'
import { Button, CardHeader, Container } from 'reactstrap';
import AdminNav from '../Component/Admin/AdminNav';
import DeleteProfile from '../Component/Admin/DeleteProfile';
import ProfileForm from '../Component/Admin/ProfileForm';
import UpdateProfile from '../Component/Admin/UpdateProfile';
import Axios from 'axios'
import { toast } from 'react-toastify';
import { userContext } from '../context/context';
import { Redirect } from 'react-router-dom';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Modal from '@material-ui/core/Modal';


export default function EditProfile() {

    const [isLoading, setIsLoading] = useState(true);
    const [open, setOpen] = React.useState(false);
    const [profile, setProfile] = useState([]);
    const context = useContext(userContext);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    useEffect(() => {
        async function getProfileList() {
            try {
                let res = await Axios({
                    method: "GET",
                    url: "http://localhost:5000/api/user/",
                });
                if (res.status === 200) {
                    setIsLoading(false);
                    setProfile(res.data);
                    console.log(res.data);
                }

            } catch (error) {
                toast('Somthing Went Wrong', { type: "info", position: "bottom-center" });
                setIsLoading(false);
            }

        }
        getProfileList();
    }, [])
    return (

        <div>
            {context.token ? (
                <>
                    <AdminNav />
                    <Container>
                        <CardHeader className="mt-5 d-flex justify-content-between">
                            <h4>
                                <strong><b>Profile Management</b></strong>
                            </h4>
                            <Button className="btn btn-light" onClick={handleOpen}>
                                <Icon className="fa fa-plus-circle" style={{ fontSize: 30, color: "green" }} />
                            </Button>
                        </CardHeader>
                        {isLoading ? (
                            <div className="text-center mt-5">
                                <CircularProgress />
                            </div>
                        ) : (
                                <>
                                    <div className="shadow mt-2">
                                        <table className="table">
                                            <thead>
                                                <tr>
                                                    <th scope="col">Image</th>
                                                    <th scope="col">Name</th>
                                                    <th scope="col">CreatedAt</th>
                                                    <th scope="col">UpdatedAt</th>
                                                    <th scope="col">ID</th>
                                                    <th scope="col">Statue</th>
                                                    <th scope="col">Options</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {profile.length !== 0 ? (
                                                    <>
                                                        {profile.map((e) => {
                                                            return (
                                                                <tr key={e._id}>
                                                                    <th scope="row"><img src={"http://localhost:5000/" + e.image} height="30" width="30" alt="" /></th>
                                                                    <td>{e.name}</td>
                                                                    <td>{e.updatedAt}</td>
                                                                    <td>{e.createdAt}</td>
                                                                    <td>{e._id}</td>
                                                                    <td>{e.draft ? (<Button className="btn btn-success">Activate</Button>) : (<Button className="btn btn-primary">InActivate</Button>)}</td>
                                                                    <td>
                                                                        <section className="d-flex">
                                                                            <Button className="btn btn-light mr-2">
                                                                                <EditIcon />
                                                                            </Button>
                                                                            <Button className="btn btn-light">
                                                                                <DeleteIcon />
                                                                            </Button>
                                                                        </section>
                                                                    </td>
                                                                </tr>
                                                            )
                                                        })}
                                                    </>
                                                ) : (
                                                        <>
                                                        <tr>
                                                            <td>
                                                                <h3 className="text-center">
                                                                    <strong>
                                                                        <b>
                                                                            No Profiles Found
                                                                    </b>
                                                                    </strong>
                                                                </h3>
                                                            </td>
                                                        </tr>
                                                        </>
                                                    )}


                                            </tbody>
                                        </table>
                                    </div>
                                    <Modal
                                        open={open}
                                        onClose={handleClose}
                                        backdrop="static"
                                        keyboard={false}
                                    >
                                        <ProfileForm />
                                    </Modal>
                                </>

                            )}

                    </Container>
                </>
            ) : (
                    <Redirect to="/admin" />
                )}

        </div>
    )
}
